mapper.select:
select newsId, type, title, content, url, createTime, modifyTime, lastViewTime, viewCount
from news


		newsId = #{newsId}


		and type = #{type}


		and title = #{title}


	 	LIMIT #{start},#{pageNum};

select count(1)
from news


		newsId = #{newsId}


		and type = #{type}


		and title = #{title}

mapper.update:
update news
type=#{type}, title=#{title}, content=#{content}, url=#{url}, modifyTime=#{modifyTime}, lastViewTime=#{lastViewTime}, viewCount=#{viewCount}
where newsId = #{newsId}
